tokenizer = T5Tokenizer.from_pretrained("rinna/japanese-roberta-base")
tokenizer.do_lower_case = True


model_names = ["tiny", "base", "small", "medium"]
whisper_names = ["faster-whisper", "insert-whisper"]
predic = [predictions_faster, predictions_insert]
for index in range(len(whisper_names)):
    for model in range(len(model_names)):
        print(f"{whisper_names[index]}: {model_names[model]}")
        total_bleu = 0
        total_bertscore = 0
        for text in range(8):
            pre = predic[index][model][text]
            ref = references[model][text]
            while len(pre) != len(ref):
                if len(pre) < len(ref):
                    pre += "#"
                if len(pre) > len(ref):
                    pre = pre[:-1]
            # BLEU
            results = bleu.compute(predictions = [pre], references = [ref], tokenizer=tokenizer.tokenize)
            total_bleu += results['bleu'] * 100
            # BERTScore
            P, R, F1 = score(pre, ref, lang="ja")
            total_bertscore += F1.mean() * 100
            print(f"{text}文目   BLEU: {results['bleu']*100}   f1_score: {F1.mean()*100}")
        print(f"全体    BLEU平均: {total_bleu/8}   BERTScore平均: {total_bertscore/8}")
        print(f"\n")


